     function Resize()
    {
        var clientW = document.body.clientWidth;
        var clientH = document.documentElement.scrollHeight;  
        var inittop = '0';
        var topline = document.getElementById("topline");
        var footer = document.getElementById("footer_masterpage");      
       
        if( inittop != '0' && inittop != "" ) 
        {
             footer.style.top = inittop + "px";    
            
             if(clientW <= 995)
            { 
            footer.style.width = "994px";
            topline.style.width = "994px";
            }
            else
            {
               footer.style.width = clientW;
               topline.style.width = clientW;
            }                 
        }
        else
        {
            if(clientW <= 995)
            { 
            footer.style.width = "994px";
            topline.style.width = "994px";
            }
            else
            {
               footer.style.width = clientW;
               topline.style.width = clientW;
            }
            if(clientH<=800)
            {
                footer.style.top = "801px";
            }
            else
            {
                footer.style.top = clientH + "px";
            }
        }        
    }
    
    window.onresize = Resize;
    Resize();
